<span class="line"><span style="color:#F78C6C;">WHEN</span><span style="color:#BABED8;"> (</span><span style="color:#F78C6C;">2</span><span style="color:#89DDFF;">+</span><span style="color:#F78C6C;">2</span><span style="color:#BABED8;">)</span><span style="color:#89DDFF;">&lt;&gt;</span><span style="color:#F78C6C;">4</span><span style="color:#F78C6C;"> THEN</span><span style="color:#89DDFF;"> &#39;</span><span style="color:#C3E88D;">not OK</span><span style="color:#89DDFF;">&#39;</span><span style="color:#F78C6C;"> END</span><span style="color:#F78C6C;"> AS</span><span style="color:#F78C6C;"> STATUS</span><span style="color:#BABED8;">;</span></span>
