import{_ as e,c as a,o as r,V as s}from"./chunks/framework.SV1ROkXV.js";const _=JSON.parse('{"title":"webassembly","description":"","frontmatter":{"title":"webassembly"},"headers":[],"relativePath":"frontend/wasm/base.md","filePath":"frontend/wasm/base.md","lastUpdated":1709440279000}'),t={name:"frontend/wasm/base.md"},l=s('<h1 id="webassembly" tabindex="-1">webassembly <a class="header-anchor" href="#webassembly" aria-label="Permalink to &quot;webassembly&quot;">​</a></h1><p>// 待补充...</p><p>WebAssembly是一种运行在现代网络浏览器中的新型代码，并且提供新的性能特性和效果。它设计的目的不是为了手写代码而是为诸如 C、C++和 Rust 等低级源语言提供一个高效的编译目标。</p><p>对于网络平台而言，这具有巨大的意义——这为客户端 app 提供了一种在网络平台以接近本地速度的方式运行多种语言编写的代码的方式；在这之前，客户端 app 是不可能做到的。</p><p>而且，你在不知道如何编写 WebAssembly 代码的情况下就可以使用它。WebAssembly 的模块可以被导入的到一个网络 app（或 Node.js）中，并且暴露出供 JavaScript 使用的 WebAssembly 函数。JavaScript 框架不但可以使用 WebAssembly 获得巨大性能优势和新特性，而且还能使得各种功能保持对网络开发者的易用性。</p><h2 id="用途" tabindex="-1">用途 <a class="header-anchor" href="#用途" aria-label="Permalink to &quot;用途&quot;">​</a></h2><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><h2 id="参考文档" tabindex="-1">参考文档 <a class="header-anchor" href="#参考文档" aria-label="Permalink to &quot;参考文档&quot;">​</a></h2><ul><li><a href="https://developer.mozilla.org/zh-CN/docs/WebAssembly/Concepts" target="_blank" rel="noreferrer">WebAssembly MDN</a></li><li><a href="https://webassembly.org/" target="_blank" rel="noreferrer">WebAssembly官网</a></li><li><a href="https://www.wasm.com.cn/" target="_blank" rel="noreferrer">WebAssembly中文网</a></li><li><a href="https://github.com/WebAssembly/design" target="_blank" rel="noreferrer">WebAssembly Design</a></li><li><a href="https://webassembly.github.io/spec/core/index.html" target="_blank" rel="noreferrer">WebAssembly Specification</a></li><li><a href="https://www.ruanyifeng.com/blog/2017/09/asmjs_emscripten.html" target="_blank" rel="noreferrer">asm.js 和 Emscripten 入门教程</a></li><li><a href="https://www.cnblogs.com/detectiveHLH/p/9928915.html" target="_blank" rel="noreferrer">WebAssembly完全入门</a></li><li><a href="https://github.com/ErosZy/md/blob/master/WebAssembly%E4%B8%93%E6%A0%8F/1.%E6%B5%85%E8%BF%B0WebAssembly%E5%8E%86%E5%8F%B2.md" target="_blank" rel="noreferrer">浅谈WebAssembly历史</a></li><li><a href="https://codechina.gitcode.host/programmer/2017/programmer-2017-55.html" target="_blank" rel="noreferrer">WebAssembly 初步探索</a></li><li><a href="https://www.cnblogs.com/jixiaohua/p/10425805.html" target="_blank" rel="noreferrer">认识WebAssembly</a></li></ul>',9),b=[l];function o(i,n,m,h,c,p){return r(),a("div",null,b)}const f=e(t,[["render",o]]);export{_ as __pageData,f as default};
